document.querySelectorAll(".accordion-item__trigger").forEach(t=>{t.addEventListener("click",()=>{var e=t.parentNode,e=(e.classList.contains("accordion-item--active")?e.classList.remove("accordion-item--active"):(document.querySelectorAll(".accordion-item").forEach(e=>{e.classList.remove("accordion-item--active")}),e.classList.add("accordion-item--active")),t.nextElementSibling);e.style.maxHeight?document.querySelectorAll(".accordion-item__content").forEach(e=>e.style.maxHeight=null):(document.querySelectorAll(".accordion-item__content").forEach(e=>e.style.maxHeight=null),e.style.maxHeight=e.scrollHeight+"px")})});